    //
    document.addEventListener("DOMContentLoaded", function() {
  var menuItems = document.querySelectorAll('#nav-primary .nav-item');
  var currentParent = null;

  menuItems.forEach(function(item) {
    var text = item.getAttribute('data-text');

    if (text && text.startsWith('-')) {
      // Nu c du "-" v c mc cha pha trc
      if (currentParent) {
        // To <ul> con nu cha c
        var submenu = currentParent.querySelector('ul');
        if (!submenu) {
          submenu = document.createElement('ul');
          submenu.classList.add('dropdown-menu'); // Class ca Bootstrap
          currentParent.appendChild(submenu);
          currentParent.classList.add('dropdown'); // Thm class  hin th mi tn
        }
        // Di chuyn mc con vo trong <ul>
        item.style.display = 'block'; // Hin th li
        item.innerHTML = item.innerHTML.replace('-', ''); // Xa du gch ngang
        submenu.appendChild(item);
      }
    } else {
      // Nu khng c du "-", y l mc cha
      currentParent = item;
    }
  });
});
